import React from 'react';
import { motion } from 'framer-motion';
import '../App.css';

const skillCategories = {
  'Programming Languages': [
    'Basic Python',

  ],
  'Databases': [
    'MySQL',
    'PostgreSQL',
    'MongoDB',
    'NoSQL',
  ],
  'Frameworks & Libraries': [
    'Basic React.js',
  ],
  'Cloud & Platforms': [
    'Amazon EC2',
    'Amazon S3',
    'Elastic Beanstalk',
    'Heroku',
    'Vercel',
    'GitHub Pages'
  ],
  'DevOps & Tools': [
    'Linux (Ubuntu)',
    'Git & GitHub',
    'Docker',
    'Kubernetes',
    'Jenkins CI/CD',
    'Ansible',
    'Terraform',
    'SonarQube',
    'Prometheus',
    'Grafana',
  ],
  'Cybersecurity & Forensics': [
    'Nmap',
    'Wireshark',
    'Burp Suite',
    'OWASP ZAP',
    'ProxyChains',
    'Kali Linux',
    'Forensic Data Analysis with Python'
  ]
};

export default function Skills() {
  return (
    <motion.section
      id="skills"
      className="container skills-section text-center my-5 py-5"
      initial={{ opacity: 0, y: 40 }}
      whileInView={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.6 }}
      viewport={{ once: true }}
    >
      <h2 className="section-title mb-4">Skills</h2>

      {Object.entries(skillCategories).map(([category, skills], index) => (
        <div key={index} className="mb-4">
          <h5 className="text-info mb-3">{category}</h5>
          <div className="skills-wrapper d-flex flex-wrap justify-content-center gap-3">
            {skills.map((skill, i) => (
              <motion.div
                key={i}
                className="skill-badge"
                whileHover={{ scale: 1.1 }}
                transition={{ type: 'spring', stiffness: 300 }}
              >
                {skill}
              </motion.div>
            ))}
          </div>
        </div>
      ))}
    </motion.section>
  );
}
